void PID(ArchiveData * ad, CurrentControl * c){
	static float e0;
	static float e1;
	static float e2;
	if(ad == NULL) return;
	e0 = ad->yzad - ad->yk[M( 0)];
	e1 = ad->yzad - ad->yk[M(-1)];
	e2 = ad->yzad - ad->yk[M(-2)];
	setCurrentControlIncrement(c,4.950000*e0 + -4.050000*e1 + 0.000000*e2); // algorytm przyrostowy
}

